import React, { Component } from 'react';
import AppHeader from "../app-header";
import SearchPanel from "../search-panel";
import TodoList from "../todo-list";
import ItemStatusFilter from "../item-status-filter";
import AddItemForm from "../add-item-form";

import './app.css';

export default class App extends Component {

    maxId = 100;

    state = {
        todoData: [
            { label: 'Drink Coffee', important: false, done: false, id: 1 },
            { label: 'Make Awesome App', important: true, done:false, id: 2 },
            { label: 'Have a lunch', important: false, done:false, id: 3 }
        ]
    };

    deleteItem = (id) => {
        this.setState(({ todoData }) => {
            const itemIndex = todoData.findIndex((item) => item.id === id);

            const newArray = [
                ...todoData.slice(0, itemIndex),
                ...todoData.slice(itemIndex + 1)
            ];

            return {
                todoData: newArray
            };
        });
    };

    createTodoItem(label) {
        return {
            label,

        };
    }

    addItem = (text) => {
        const newItem = {
            id: this.maxId++,
            important: false,
            label: text,
        };

        this.setState(( { todoData } ) => {
            const newTodoData = [
                ...todoData,
                newItem
            ];

            return {
                todoData: newTodoData
            };
        });
    };

    toggleImportant = (id) => {
        console.log('Toggle important', id);
    };

    toggleDone = (id) => {
        console.log('Toggle done', id);
    };

    render() {
        return (
            <div className="todo-app">
                <AppHeader toDo={1} done={3} />
                <div className="top-panel d-flex">
                    <SearchPanel />
                    <ItemStatusFilter />
                </div>

                <TodoList
                    todoItemsData={this.state.todoData}
                    onDeleted={ this.deleteItem }
                    onToggleImportant={ this.toggleImportant }
                    onToggleDone={ this.toggleDone }
                />

                <AddItemForm onAddItem={ this.addItem }/>
            </div>
        );
    }
}
