import React, { Component } from 'react';
import AppHeader from "../app-header";
import SearchPanel from "../search-panel";
import TodoList from "../todo-list";
import ItemStatusFilter from "../item-status-filter";
import AddItemForm from "../add-item-form";

import './app.css';

export default class App extends Component {

    maxId = 100;

    state = {
        todoData: [
            this.createTodoItem('Drink Coffee'),
            this.createTodoItem('Make Awesome App'),
            this.createTodoItem('Have a lunch')
        ]
    };

    createTodoItem(label) {
        return {
            label,
            important: false,
            done: false,
            id: this.maxId++
        };
    }

    deleteItem = (id) => {
        this.setState(({ todoData }) => {
            const itemIndex = todoData.findIndex((item) => item.id === id);

            const newArray = [
                ...todoData.slice(0, itemIndex),
                ...todoData.slice(itemIndex + 1)
            ];

            return {
                todoData: newArray
            };
        });
    };

    addItem = (text) => {
        const newItem = this.createTodoItem(text);

        this.setState(( { todoData } ) => {
            const newTodoData = [
                ...todoData,
                newItem
            ];

            return {
                todoData: newTodoData
            };
        });
    };

    toggleImportant = (id) => {
        this.setState(({ todoData }) => {
            const itemIndex = todoData.findIndex((item) => item.id === id);
            const item = todoData[itemIndex];

            const newItem = { ...item, important: ! item.important };

            const newTodoData = [
                ...todoData.slice(0, itemIndex),
                newItem,
                ...todoData.slice(itemIndex + 1)
            ];

            return {
                todoData: newTodoData
            };
        });
    };

    toggleDone = (id) => {
        this.setState(({ todoData }) => {
            const itemIndex = todoData.findIndex((item) => item.id === id);
            const item = todoData[itemIndex];

            const newItem = { ...item, done: ! item.done };

            const newTodoData = [
                ...todoData.slice(0, itemIndex),
                newItem,
                ...todoData.slice(itemIndex + 1)
            ];

            return {
                todoData: newTodoData
            };
        });
    };

    toggleProperty(items, id, propertyName) {
        const itemIndex = items.findIndex((item) => item.id === id);
        const item = items[itemIndex];

        const newItem = { ...item, [propertyName]: ! item[propertyName] };

        const newTodoData = [
            ...todoData.slice(0, itemIndex),
            newItem,
            ...todoData.slice(itemIndex + 1)
        ];

        return {
            todoData: newTodoData
        };
    }

    render() {

        const { todoData } = this.state;
        const countDone = todoData.filter((item) => item.done).length;
        const todoCount = todoData.length - countDone;

        return (
            <div className="todo-app">
                <AppHeader toDo={todoCount} done={countDone} />

                <div className="top-panel d-flex">
                    <SearchPanel />
                    <ItemStatusFilter />
                </div>

                <AddItemForm onAddItem={ this.addItem }/>

                <TodoList
                    todoItemsData={todoData}
                    onDeleted={ this.deleteItem }
                    onToggleImportant={ this.toggleImportant }
                    onToggleDone={ this.toggleDone }
                />
            </div>
        );
    }
}
